    private void btnCalculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculoActionPerformed
        //Entrada 
        calc.setAltura(Double.parseDouble(txtAltura.getText()));
        calc.setPeso(Double.parseDouble(txtPeso.getText()));

        //Processamento
        calc.CalcularImc();
        calc.ExibeStatus();

        //Saida
        txtResultado.setText(String.valueOf(df.format(calc.getResultado())));
        txtStatus.setText(String.valueOf(calc.getStatus()));
    }//GEN-LAST:event_btnCalculoActionPerformed

    private void btnLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimparActionPerformed
        calc.limpar();
        this.txtAltura.setText("");
        this.txtPeso.setText("");
        this.txtResultado.setText("");
        this.txtStatus.setText("");
        
    }//GEN-LAST:event_btnLimparActionPerformed

