    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // Entrada
        retangulo.setLadoA(Double.parseDouble(txtLadoA.getText())); 
        retangulo.setLadoB(Double.parseDouble(txtLadoB.getText()));
        
        //Processamento
        retangulo.calcularArea();
        retangulo.calcularPerimetro();
        
        //Saida
        txtPerimetro.setText(String.valueOf(retangulo.getPerimetro(WIDTH)));
        txtArea.setText(String.valueOf(retangulo.getArea(WIDTH)));
        
    }//GEN-LAST:event_btnCalcularActionPerformed

